    function userNameToInitials(user) {
        user = user.split(/@/g);
        user = user[0];
        user = user.split(/\\/g);
        user = user[user.length > 1 ? 1 : 0];
        var userWords = user.replace(/\W/g, ' ').trim(),
            word, initials = '', i;
        if (!userWords)
            userWords = user;
        userWords = userWords.split(/\b/g);
        for (i = 0; i < userWords.length; i++) {
            word = userWords[i];
            if (word)
                word = word.trim();
            if (word)
                initials += word.substring(0, 1);
            if (initials.length === 2)
                break;
        }
        return initials;
    }

